import React from 'react';
import { View, StyleSheet, Text } from 'react-native';


function PlantsCirclePreview(props) {

    const { PrevieWidth, margin, plant, isSelected } = props;

    return (
        <View style={{
            width: PrevieWidth,
            height: '80%',
            margin: margin,
            justifyContent: 'start',
            alignItems: 'center',
        }}>
            <View style={styles.circlePreview}></View>
            {isSelected && <Text style={styles.addOrRemove}>-</Text>}
            {!isSelected && <Text style={styles.addOrRemove}>+</Text>}
            <Text style={{textAlign: 'center'}}>{plant.name.split('-')[0]}</Text>
        </View>
    );
}



const styles = StyleSheet.create({
    previewContainer: {
        flex: 1,
        justifyContent: 'start',
        alignItems: 'center',

    },
    circlePreview: {
        backgroundColor: 'pink',
        width: 65,
        height: 65,
        borderRadius: '100%',
        marginBottom: '6%',
    },
    addOrRemove:{
        width: 20,
        height: 20,
        backgroundColor: 'green',
        position: 'absolute',
        top:45,
        right:0,
        borderRadius: '100%',
        textAlign: 'center',
        color: 'white'
    }
})

export default PlantsCirclePreview;
