import { createSlice, current } from "@reduxjs/toolkit";

const orderPlantsReducer = createSlice({
  name: "orderPlantsReducer",

  initialState: {
    availablePlants: [
      {
        id: "cucumber_iznik1",
        name: "Cucumber - Iznik",
        imageId: "iznik"
      },
      {
        id: "tomato_sunrise1",
        name: "Tomato - Sunrise",
        imageId: "tomato_sunrise"
      },
      {
        id: "tomato_bigdena1",
        name: "Tomato - Bigdena",
        imageId: "bigdena"
      },
      {
        id: "cucumber_iznik",
        name: "Cucumber - Iznik",
        imageId: "iznik"
      },
      {
        id: "tomato_sunrise",
        name: "Tomato - Sunrise",
        imageId: "tomato_sunrise"
      },
      {
        id: "tomato_bigdena",
        name: "Tomato - Bigdena",
        imageId: "bigdena"
      }
    ],
    selectedPlants: [{
      id: "tomato_sunrise",
      name: "Tomato - Sunrise",
      imageId: "tomato_sunrise"
    },
    {
      id: "tomato_bigdena",
      name: "Tomato - Bigdena",
      imageId: "bigdena"
    }],
    plantsCategories: [],
    newOrder: []
  },

  reducers: {

    getAvailablePlants: (state, action) => {
    },
    addOrRemoveSelectedPlant: (state, action) => {

      const { id, isSelected } = action.payload;

      var deleteFrom = isSelected ? 'selectedPlants' : 'availablePlants';
      const addTo = isSelected ? 'availablePlants' : 'selectedPlants';

      const objPlant = current(state[addTo]).find(item => item.id === id);
      const objPlantIdx = current(state[deleteFrom]).findIndex(item => item.id === id);

      state[deleteFrom].splice(objPlantIdx, 1);
      state[addTo].push(objPlant);
    },


  }


})

export const { addOrRemoveSelectedPlant, getAvailablePlants } = orderPlantsReducer.actions;
export default orderPlantsReducer.reducer;
