import React from 'react';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import { useDispatch, useSelector } from 'react-redux';


function SaveOrder(props) {

    const dispatch = useDispatch();
    const quantityLimit = useSelector(store => store.orderPlantsReducer.quantityLimit);

    function orderSaved() {
        console.log('its save');
    }

    return (
        <View>

            <View style={(quantityLimit === 5) ? styles.buttonContainer : styles.buttonContainerdisabled}>
                <TouchableOpacity style={styles.button} disabled={quantityLimit !== 5} onPress={() => { orderSaved() }} ><Text style={{ fontWeight: 'bold' }}>SAVE CHANGES</Text></TouchableOpacity>
            </View>
            <View style={styles.orderSavedConfirmation}>
                <View style={styles.confirmationTextContainer}>
                    <Text>Awesome!</Text>
                    <Text>Your order completed!</Text>
                </View>
            </View>
        </View>
    )
}


const styles = StyleSheet.create({
    buttonContainer: {
        flex: 1,
        width: '100%',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: "rgba(254,253,255,1)",

    },
    buttonContainerdisabled: {
        flex: 1,
        width: '100%',
        alignItems: 'center',
        justifyContent: 'center',
        opacity: 0.5,
    },
    button: {
        width: '80%',
        height: '30%',
        alignItems: 'center',
        borderRadius: 8,
        justifyContent: 'center',
        backgroundColor: "rgba(148,217,234,1)",
    },
    orderSavedConfirmation: {
        width: '100%',
        height: '100%',
        backgroundColor: "rgba(148,217,234,0.5)",
        position: "fixed",
        top: 0,
        justifyContent: "center",
        alignItems: "center"
    },
    confirmationTextContainer: {
        width: '70%',
        height: '30%',
        backgroundColor: "white",
        justifyContent: "center",
        alignItems: "center",
        borderRadius:15
    }
})

export default SaveOrder;
