import { createSlice, current } from "@reduxjs/toolkit";

const orderPlantsReducer = createSlice({
  name: "orderPlantsReducer",

  initialState: {
    availablePlants: [
      {
        id: "cucumber_iznik1",
        name: "Cucumber - Iznik",
        imageId: "iznik"
      },
      {
        id: "tomato_sunrise1",
        name: "Tomato - Sunrise",
        imageId: "tomato_sunrise"
      },
      {
        id: "tomato_bigdena1",
        name: "Tomato - Bigdena",
        imageId: "bigdena"
      },
      {
        id: "cucumber_iznik",
        name: "Cucumber - Iznik",
        imageId: "iznik"
      },
      {
        id: "tomato_sunrise",
        name: "Tomato - Sunrise",
        imageId: "tomato_sunrise"
      },
      {
        id: "tomato_bigdena",
        name: "Tomato - Bigdena",
        imageId: "bigdena"
      }
    ],
    selectedPlants: [{
      id: "tomato_sunrise",
      name: "Tomato - Sunrise",
      imageId: "tomato_sunrise"
    },
    {
      id: "tomato_bigdena",
      name: "Tomato - Bigdena",
      imageId: "bigdena"
    }],
    plantsCategories: [],
    newOrder: []
  },

  reducers: {

    getAvailablePlants: (state, action) => {
    },
    addOrRemoveSelectedPlant: (state, action) => {

      const { id, isSelected } = action.payload;

      if (isSelected) {
        const objPlant = current(state.availablePlants).find(item => item.id === id);
        const objPlantIdx = current(state.availablePlants).findIndex(item => item.id === id);
        
        console.log('state', current(state.availablePlants));
        state.selectedPlants.splice(objPlant,1);
        state.availablePlants.push(objPlant,1);
        console.log('state', current(state.availablePlants));

        // console.log('state', current(state));
        // console.log('objPlantIdx', objPlantIdx);
      }


    },


  }


})

export const { addOrRemoveSelectedPlant, getAvailablePlants } = orderPlantsReducer.actions;
export default orderPlantsReducer.reducer;
