import React from 'react';
import { View, StyleSheet, Text, TouchableOpacity } from 'react-native';
import PlantsCirclePreview from './PlantsCirclePreview';

function Categories(props) {
    return (
        <View style={styles.categoriesContainer}>
            <View style={styles.categoryPlants}>
                <PlantsCirclePreview/>
                <PlantsCirclePreview/>
                <PlantsCirclePreview/>
                <PlantsCirclePreview/>
                <PlantsCirclePreview/>
                <PlantsCirclePreview/>
                <PlantsCirclePreview/>
                <PlantsCirclePreview/>
                <PlantsCirclePreview/>
            </View>
            <View style={styles.buttonContainer}>
                <TouchableOpacity style={styles.button} disabled={true}><Text style={{ fontWeight: 'bold' }}>SAVE CHANGES</Text></TouchableOpacity>
            </View>
        </View>
    );
}

const styles = StyleSheet.create({

    categoriesContainer: {
        flex:1,
        height: '30%',
        width: '100%',
        alignItems: 'center',
        backgroundColor: 'rgba(239,234,253,1)',
        flexDirection:'row'
    },
    categoryPlants: {
        height: '70%',
        width: '100%',
    },
    buttonContainer: {
        flex: 1,
        width: '100%',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: 'yellow',
        backgroundColor: "rgba(254,253,255,1)",

    },
    button: {
        backgroundColor: 'blue',
        width: '80%',
        height: '55%',
        alignItems: 'center',
        borderRadius: 8,
        justifyContent: 'center',
        backgroundColor: "rgba(148,217,234,1)",


    }

})

export default Categories;
