import React from 'react';
import { View, StyleSheet, Text, FlatList } from 'react-native';
import PlantsCirclePreview from './PlantsCirclePreview';
import { useDimensions } from '@react-native-community/hooks';
import { useSelector, useDispatch } from 'react-redux';

function SelectedPlants(props) {

    const PrevieWidth = useDimensions().screen.width;
    const selectedPlantsList = useSelector(store => store.orderPlantsReducer.selectedPlants);

    return (
        <View style={styles.selectedPlants}>
            <FlatList
                keyExtractor={(item) => item.id}
                horizontal={true}
                data={selectedPlantsList}
                renderItem={({ item }) => (
                    <PlantsCirclePreview PrevieWidth={PrevieWidth / 6} margin={4} plant={item} />
                )} />
        </View>
    );
}

const styles = StyleSheet.create({
    selectedPlants: {
        backgroundColor: 'blue',
        width: '100%',
        height: '30%',
        flexDirection: 'row',
        justifyContent: 'space-evenly',
    }
})

export default SelectedPlants;
